    "create_table_query = '''CREATE TABLE IF NOT EXISTS {0}.{1} \n",
    "                                (name varchar(250),\n",
    "                                url varchar(250), \n",
    "                                concept varchar(250),\n",
    "                                relevance varchar(250)\n",
    "                                )'''.format(db_name, table_name)\n",
    "cursor.execute(create_table_query)\n",
    "cursor.close()"
